<template>
  <canvas id="mask" :width="luckWindow.w" :height="luckWindow.h"></canvas>
</template>

<script>
export default {
  props: ["luckWindow", "scrollTop", "onScratchEnd", "client"],
  methods: {},
  mounted() {
    const self = this;
    const { w, h } = this.luckWindow;
    var canvas = document.getElementById("mask");
    var context = canvas.getContext("2d");
    context.fillStyle = "#a8a8a8";
    context.fillRect(0, 0, w, h);

    var img = new Image();
    img.src =
      "data:image/png;base64,";
    img.onload = function() {
      context.drawImage(img, 0, 0, w, h);
      context.globalCompositeOperation = "destination-out";
    };

    // 鼠标按下 增加mousemove的事件监听
    canvas.addEventListener("mousedown", drawArcMouseHandle);
    canvas.addEventListener("mouseup", function(event) {
      // 鼠标抬起之后，把mousemove的事件监听撤销掉
      this.removeEventListener("mousemove", mousemoveHandle);
    });
    // 根据鼠标的move画圆
    function drawArcMouseHandle(event) {
      event.preventDefault();
      event.target.addEventListener("mousemove", mousemoveHandle);
    }
    // 为了能够移除movesemove的事件需要单独处理一下
    function mousemoveHandle(event) {
      event.preventDefault();
      drawArcByPoint(event.pageX, event.pageY);
    }
    // 监听 touchmove
    canvas.addEventListener("touchmove", drawArcTouchHandle);
    // 根据触摸点画圆
    function drawArcTouchHandle(event) {
      event.preventDefault();
      var touch = event.touches[0];
      drawArcByPoint(touch.pageX, touch.pageY);
    }
    // 根据某个点在canvas上画圆
    // x 坐标和 y 坐标 两个坐标是触摸点的坐标而不是画圆的圆心
    // 圆心通过计算得出
    function drawArcByPoint(x, y) {
      context.beginPath();
      const x1 = self.client == "pad" ? 0.135714285714286 : 0.1785714285714286;
      const y1 = self.client == "pad" ? 2.497297297297297 : 2.297297297297297;
      context.arc(
        x - canvas.offsetLeft - x1 * w,
        y - canvas.offsetTop - y1 * h + self.scrollTop,
        20,
        0,
        Math.PI * 2
      );
      context.closePath();
      context.fillStyle = "#dddddd";
      context.fill();
      checkComplete();
    }
    // 判断是否完成刮奖 点数大于80%
    function checkComplete() {
      var imgData = context.getImageData(0, 0, w, h);

      var pxData = imgData.data; // 获取字节数据
      var len = pxData.length; // 获取字节长度
      var count = 0; // 记录透明点的个数
      // 主要的思想是 一个像素由四个数据组成，每个数据分别是 rgba() 所以第四个数据 a 表示alpha透明度
      for (var i = 0; i < len; i += 4) {
        var alpha = pxData[i + 3]; // 获取每个像素的透明度
        if (alpha < 10) {
          // 透明度小于10
          count++;
        }
      }
      var percent = count / (len / 4); // 计算百分比
      // 如果百分比大于0.8 则表示成功
      if (percent >= 0.6) {
        showResult();
      }
    }
    // 显示刮奖结果
    function showResult(msg) {
      msg = msg || "刮奖结束";
      self.onScratchEnd && self.onScratchEnd();
    }
  }
};
</script>
